INSERT INTO `boxes` VALUES(1, '<div class="register-now">\r\n<a href="http://tcopencircuit.org/drupal_daze" title="Open Circuit Drupal Daze Registration Page(not this will take you to the Open Circuit website)"><img src="/sites/all/themes/dd_custom/images/register_button.gif" alt="Register Now for Drupal Daze" /></a>\r\n</div>', 'Register Now', 2);
INSERT INTO `boxes` VALUES(2, 'Bring your old, small electronics to Drupal Daze to be recycled by <a href="/partners/ocean-tech" title="Ocean Tech">Ocean Tech</a>!', 'Ocean Tech Info', 1);
INSERT INTO `boxes` VALUES(3, 'Want to chat with other Drupal Daze attendees now or during the event?  Join up on IRC!  <a href="/live-chat" title="Drupal Daze Live Chat">Drupal Daze Live Chat Details</a>.', 'Live Chat', 1);
INSERT INTO `boxes` VALUES(4, 'Join us at <a href="http://grumpys-bar.com/">Grumpy''s Bar & Grill</a> (the side room is reserved for us) at 7 pm Saturday night to unwind after a long day of Drupalling. Grumpy''s is located at <a href="http://maps.google.com/maps?f=q&hl=en&geocode=&q=1111+Washington+Ave.+S.+in+Minneapolis&sll=44.959036,-93.23623&sspn=0.007425,0.018797&ie=UTF8&ll=44.976608,-93.253069&spn=0.007423,0.018797&z=16&iwloc=addr" title="1111 Washington Ave. S. in Minneapolis">1111 Washington Ave. S. in Minneapolis</a>. ', 'Grumpy''s social event', 1);
INSERT INTO `comments` VALUES(2, 0, 11, 0, 'Hello !', 'I''m very interested in the subject of this conference (I want to create a full flash website with only Drupal for back office, users & roles management etc...)\r\n\r\nBut I can''t be there ! Will this presentation be recorded like an on-demand webcast ?\r\n\r\nThanks !', '64.18.85.70', 1216325316, 0, 1, '01/', '', '@localhost', '');
INSERT INTO `comments` VALUES(3, 0, 66, 0, 'More about session', 'We''ll be looking at community building tools within Drupal and how they are contributing to the new journalism model on <a href="http://www.tcdailyplanet.net">http://www.tcdailyplanet.net </a>.\r\n\r\nBoth Drupal and non-Drupal community building will be discussed.  We''ll also take a look at the direction the TC Daily Planet is planning to go with increased visitor participation options:  geocoding, syndication, bookmarking, live chats and video streaming, etc.\r\n\r\nVery informal.  Questions and discussion encouraged.', '216.250.182.77', 1216423651, 0, 1, '01/', '', '@localhost', '');
INSERT INTO `comments` VALUES(4, 0, 65, 0, 'My quick bio', 'Barry works as a Drupal developer and trainer for Advantage Labs, Inc.  He is also on the board of the Twin Cities Media Alliance, the nonprofit media reform organization which publishes the TC Daily Planet website.\r\n\r\nAdvantage Labs provides managed hosting for Drupal sites, training for users and developers and sponsors the monthly free TC Drupal Users Group meetings.  ', '216.250.182.77', 1216423909, 0, 1, '01/', '', '@localhost', '');
INSERT INTO `comments` VALUES(5, 0, 20, 0, 'Download presentation', 'http://PublicComputingServices.org/VNA.ppt', '67.130.19.190', 1216587193, 0, 1, '01/', '', '@localhost', '');
INSERT INTO `comments` VALUES(6, 0, 76, 0, 'The Power of the Drupal Community', 'It''s simply amazing the amount of people that will come together in a local area for a weekend in July and learn about.  I just want to thank everyone for making this a wonderful event.\r\n\r\nI am so glad to be a part of this amazing Twin Cities Drupal Community.  It was fantastic to see so many different people that were interested Drupal.  I hope you will leave your comments so that we cna make the next event even better.', '75.72.221.218', 1216792345, 0, 1, '01/', 'Alan Palazzolo', 'Alan Palazzolo@localhost', 'http://alanpalazzolo.com/');
INSERT INTO `comments` VALUES(7, 0, 76, 0, 'glad to get into the drupal comm', 'This only being my 2nd drupal event in the cities, I too am super excited when I think of how easy it is to learn from other people here.  I loved this past weekend and totally appreciate how everyone is willing to help you out no matter how much Drupal you may already know.', '128.101.178.119', 1216851298, 0, 1, '02/', 'Thomas Bishop', 'Thomas Bishop@localhost', 'http://www.mndaily.com');
INSERT INTO `comments` VALUES(8, 0, 76, 0, 'Thanks everyone!', 'I had a great time this weekend, and thanks everyone for coming! The best part for me was meeting other people so enthusiastic & willing to share their knowledge and experience. And I like seeing how far people are pushing the software, expanding it in all different directions.\r\n\r\nHope to see everyone again soon at a local user group meetup!\r\n\r\nAmanda', '67.130.19.190', 1216851976, 0, 1, '03/', 'Amanda L', 'Amanda L@localhost', 'http://www.pinkslipmedia.org');
INSERT INTO `comments` VALUES(9, 0, 76, 0, 'Glad I Made the Drive!', 'Well worth the drive from Madison and I would do it again!\r\n\r\nWell organized with great people to network in.  Only addition I would have liked was a chance to sit down with others and discuss challenges with current projects.', '71.150.251.94', 1216948017, 0, 1, '04/', 'Kris', 'Kris@localhost', 'http://www.kittycam.net/');
INSERT INTO `comments` VALUES(10, 0, 76, 0, 'Thanks for putting it together!', 'Alan - thanks much for taking the initiative to bring people together. It was a great event - happy to have had the opportunity to participate!\r\n\r\nAs far as improvements go, I do like the idea of a things-i''m-trying-to-solve-right-now discussion as suggested above by kris (http://drupaldaze.com/tell-us-how-we-did#comment-9).\r\n\r\nLikewise, tapping into the existing DrupalCamp structure (of which I know little) might be worth looking into and may bring even more participants next time: http://groups.drupal.org/drupal-camp.\r\n\r\nDrew', '24.245.1.191', 1217023567, 0, 1, '05/', 'Drew Gorton', 'Drew Gorton@localhost', 'http://www.gortonstudios.com');
INSERT INTO `comments` VALUES(11, 0, 76, 0, 'Yes -- drupaldaze was great!', 'I can''t wait for the next drupal event in our home town! Thanks you all for organizing it.', '75.72.194.105', 1217784120, 0, 1, '06/', 'Chacha', 'Chacha@localhost', 'http://artmess.org');
INSERT INTO `comments` VALUES(12, 0, 11, 0, 'When!??', 'All I can say is "when?" I am new to Drupal, but am interested in using it to allow a client to update his site.', '71.106.113.179', 1217989076, 0, 1, '02/', '', '@localhost', '');
INSERT INTO `comments` VALUES(13, 0, 76, 0, 'dsf', 'Because thanks to my friends about this author.\r\nevery time you enter the site. <a href="http://www.youtubeask.com" title="izlesene">izlesene</a> is always providing the original video.\r\nA very good site like <a href="http://www.youtubeizleyin.net" title="facebook">facebook</a> siteside. I said I love you.\r\nregards...', '78.165.215.212', 1244565541, 0, 1, '07/', 'dsfd', 'dsfd@localhost', '');
INSERT INTO `comments` VALUES(14, 0, 51, 0, 'This gave me the first basic', 'This gave me the first basic understanding for CCK. Excellent presentation for novice.', '67.207.113.34', 1246317488, 0, 1, '01/', '', '@localhost', '');
INSERT INTO `comments` VALUES(15, 0, 76, 0, 'Thanks for sharing these info', 'Thanks for sharing these info with us! I was reading something similar on <a href="http://www.youtubeizle1.com">youtube</a> website that i was researching. I will be sure to look around more. thanks...', '78.188.10.16', 1260216685, 0, 1, '08/', 'Anonymous', 'Anonymous@localhost', '');
